mapper.select:
SELECT ITEM_NO, ITEM_NAME, ITEM_CATEGORY, ITEM_COMMENT, PRICE, ORIGIN_PRICE, STOCK, DISP_STAT, SALE_STAT
  FROM ITEM
 ORDER BY ITEM_NO DESC;

SELECT ITEM_NO, ITEM_NAME, ITEM_CATEGORY, ITEM_COMMENT, PRICE, ORIGIN_PRICE, STOCK, DISP_STAT, SALE_STAT
  FROM ITEM
 WHERE ITEM_NO = #{itemNo}

SELECT ATTACH_NO, PATH, ORIGIN_NAME, FILESYSTEM_NAME, HAS_THUMBNAIL, ITEM_NO
  FRON ATTACH
 WHERE ITEM_NO = #{itemNo}

SELECT ITEM_NO, ATTACH_NO, PATH, ORIGIN_NAME, FILESYSTEM_NAME, HAS_THUMBNAIL
  FROM ATTACH
 WHERE ATTACH_NO = #{attachNo}

mapper.update:
UPDATE ITEM
   SET ITEM_NAME = #{itemName}
     , ITEM_CATEGORY = #{itemCategory}
     , ITEM_COMMENT = #{itemComment}
     , PRICE = #{price}
     , ORIGIN_PRICE = #{originPrice}
     , STOCK = #{stock}
     , DISP_STAT = #{dispStat}
     , SALE_STAT = #{saleStat}
 WHERE ITEM_NO = #{itemNo}
