mapper.insert:
INSERT INTO FAQ (
  SELLER_CHECK
, ID
, FAQ_CATEGORY
, FAQ_TITLE
, FAQ_CONTENT
, FAQ_CREATED_AT
, FAQ_MODIFIED_AT
, FAQ_HIT
) VALUES (
  2
, 'admin'
, #{faqCategory}
, #{faqTitle}
, #{faqContent}
, NOW()
, NOW()
, #{faqHit}
)

mapper.select:
SELECT FAQ_NO, SELLER_CHECK, ID, FAQ_CATEGORY, FAQ_TITLE, FAQ_CONTENT, FAQ_CREATED_AT, FAQ_MODIFIED_AT, FAQ_HIT
FROM FAQ
WHERE FAQ_NO = #{faqNo}

mapper.update:
UPDATE FAQ
SET FAQ_HIT = FAQ_HIT + 1
WHERE FAQ_NO = #{faqNo}

UPDATE FAQ
SET 
  FAQ_CATEGORY = #{faqCategory}
, FAQ_TITLE = #{faqTitle}
, FAQ_CONTENT = #{faqContent}
, FAQ_CREATED_AT = NOW()
, FAQ_MODIFIED_AT = NOW()
WHERE FAQ_NO = #{faqNo}

mapper.select:
SELECT FAQ_NO, SELLER_CHECK, ID, FAQ_CATEGORY, FAQ_TITLE, FAQ_CONTENT, FAQ_CREATED_AT, FAQ_MODIFIED_AT, FAQ_HIT FROM ( SELECT FAQ_NO, SELLER_CHECK, ID, FAQ_CATEGORY, FAQ_TITLE, FAQ_CONTENT, FAQ_CREATED_AT, FAQ_MODIFIED_AT, FAQ_HIT FROM FAQ
FAQ_CATEGORY = #{kind}
AND (${searchColumn} LIKE CONCAT('%', #{query}, '%') OR FAQ_TITLE LIKE CONCAT('%', #{query}, '%') OR FAQ_CONTENT LIKE CONCAT('%', #{query}, '%'))
AND (FAQ_TITLE LIKE CONCAT('%', #{query}, '%') OR FAQ_CONTENT LIKE CONCAT('%', #{query}, '%'))
) A
LIMIT #{begin}, #{recordPerPage}
ORDER BY FAQ_NO DESC

SELECT COUNT(F.FAQ_NO) FROM FAQ F
${searchColumn} LIKE CONCAT('%', CONCAT(#{query}, '%'))
FAQ_CATEGORY = #{kind}

SELECT COUNT(FAQ_NO)
FROM FAQ
WHERE FAQ_CATEGORY = #{kind}

SELECT DISTINCT ${searchColumn}
FROM FAQ
WHERE ${searchColumn} LIKE CONCAT(#{query}, '%')
